import React from "react";

const BiogramCard = ({ person }) => {
  const { surname, name, imagePath, shortDesc } = person;
  return (
    <a href="/">
      <div className="border-2 border-black aspect-square">
        <img
          alt={surname + name}
          src={process.env.PUBLIC_URL + `${imagePath}`}
          className="object-cover max-w-fit min-h-fit w-100 h-auto"
        />
      </div>
      <p>
        {person.name} {person.surname}
      </p>
    </a>
  );
};

export default BiogramCard;
