import React from "react";
import { NavLink, useParams } from "react-router-dom";
import biograms from "../biograms";
import parse from "html-react-parser";

const BiogramPage = () => {
  const { id } = useParams();
  const biogram = biograms[id];
  const { surname, name, life, imagePath, video } = biogram;
  return (
    <>
      <div className="flex">
        <NavLink to="/biogramList" className="underline font-bold">
          Biogramy
        </NavLink>
        <p>
          / {biogram.name} <span className="uppercase">{biogram.surname}</span>
        </p>
      </div>
      <div className="relative z-10">
        <div>
          <div className="bg-black absolute w-full h-full z-10 opacity-75"></div>
          <div className="flex absolute z-20">
            <img
              alt={surname + name}
              src={process.env.PUBLIC_URL + `${imagePath}`}
              className="object-cover h-80 m-4"
            />
            <div className="py-2 text-white text-7xl">
              <span className=" white  ">{biogram.name} </span>
              <span className="font-black uppercase">{biogram.surname}</span>
              <p>{biogram.life}</p>
            </div>
          </div>
        </div>
        <video
          className="w-full relative"
          src={`${video}`}
          autoPlay
          muted
        ></video>
      </div>
      <div className="bg-white z-20">
        <p className="font-black text-2xl py-2">
          {biogram.name} <span className="uppercase">{biogram.surname}</span>
        </p>
        {biogram.desc.map((paragraph, i) => (
          <p key={i} className="text-lg pb-3 text-justify">
            {parse(paragraph)}
          </p>
        ))}
      </div>
    </>
  );
};

export default BiogramPage;
