import React from "react";
import { NavLink } from "react-router-dom";

const BiogramCard = ({ fullName, person }) => {
  const { surname, name, imagePath, shortDesc } = person;
  return (
    <NavLink to={`/biogram/${fullName}`}>
      <div className="border-1  border-gray-400 shadow-xl justify-center flex hover:shadow-lg">
        <img
          alt={surname + name}
          src={process.env.PUBLIC_URL + `${imagePath}`}
          className="object-cover h-64"
        />
      </div>
      <div className="py-2">
        {person.name}
        <span className="uppercase font-black"> {person.surname}</span>
      </div>
    </NavLink>
  );
};

export default BiogramCard;
