import React from "react";
import { MapContainer, Marker, Popup, TileLayer } from "react-leaflet";
import { Icon, LatLngExpression } from "leaflet";
import biograms from "../../biograms";
import "leaflet/dist/leaflet.css";

const Map = () => {
  const biogramList = Object.entries(biograms);
  return (
    <MapContainer
      style={{ height: "100vh" }}
      center={[51.2861, 17.904209]}
      zoom={16}
      scrollWheelZoom={false}
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {biogramList.map((data) => {
        const [fullName, person] = data;
        const { surname, name, imagePath } = person;
        return (
          <Marker
            position={person.location as LatLngExpression}
            key={person.name}
          >
            <Popup>
              <img src={person?.imagePath ?? ""} alt={person.name} />
              <div className="absolute  bottom-0 bg-black p-4 w-full">
                {person.name}
                <span className="uppercase font-black"> {person.surname}</span>
              </div>
            </Popup>
          </Marker>
        );
      })}
    </MapContainer>
  );
};

export default Map;
