import React, { useEffect, useRef, useState } from "react";
import { MapContainer, Marker, Popup, TileLayer } from "react-leaflet";
import { LatLngBoundsExpression, LatLngExpression } from "leaflet";
import "leaflet/dist/leaflet.css";

const Map = () => {
  const murals: { name: string; location: LatLngExpression }[] = [
    {
      name: "Stanisław Kutzner",
      location: [51.28459572922375, 17.905000063729098],
    },
    {
      name: "Antoni Gabriel",
      location: [51.28482854158273, 17.912244445391178],
    },
    { name: "Tomasz Gabriel", location: [51.28632098765013, 17.9012634897601] },
    {
      name: "Ignacy Majchrzak",
      location: [51.28572817968929, 17.90471084127396],
    },
    {
      name: "Stanisław Grossek",
      location: [51.28648999055122, 17.912114123362826],
    },
    {
      name: "Melchior Grossek",
      location: [51.285630149541376, 17.902997743189374],
    },
    { name: "Jan Rybark", location: [51.28579361124551, 17.90152752908563] },
    {
      name: "Stefan Wojciechowski",
      location: [51.28398171687474, 17.91392182413354],
    },
    { name: "Jan Nowak", location: [51.285910975239716, 17.908082681648615] },
    {
      name: "Michał Przywara",
      location: [51.286094725553596, 17.913834504522413],
    },
  ];

  return (
    <MapContainer
      style={{ height: "100vh" }}
      center={[51.2861, 17.904209]}
      zoom={16}
      scrollWheelZoom={false}
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {murals.map((mural) => {
        return (
          <Marker position={mural.location}>
            <Popup>{mural.name}</Popup>
          </Marker>
        );
      })}
    </MapContainer>
  );
};

export default Map;
