import React from "react";
import { MapContainer, Marker, Popup, TileLayer } from "react-leaflet";
import { Icon, LatLngExpression } from "leaflet";
import biograms from "../../biograms";
import "leaflet/dist/leaflet.css";
import { NavLink } from "react-router-dom";

const Map = () => {
  const biogramList = Object.entries(biograms);
  return (
    <MapContainer
      style={{ height: "100vh" }}
      center={[51.2861, 17.904209]}
      zoom={16}
      scrollWheelZoom={false}
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {biogramList.map((data) => {
        const [fullName, person] = data;
        const { surname, name, imagePath, birthdate, deathdate } = person;
        const lifeLabel = `[${birthdate}-${deathdate}]`;
        return (
          <Marker position={person.location as LatLngExpression} key={fullName}>
            <NavLink to={`/biogram/${fullName}`} className="hover:shadow-lg">
              <Popup>
                <img src={imagePath} alt={fullName} />
                <p className="font-black my-0">{fullName}</p>
                <p className="text-sm">{lifeLabel}</p>
              </Popup>
            </NavLink>
          </Marker>
        );
      })}
    </MapContainer>
  );
};

export default Map;
